export class ParkingDashboardComponent {
  constructor(parkingService) {
    this.parkingService = parkingService;
    this.parkingLots = [];
    this.filteredLots = [];
    this.selectedLot = null;
    this.stats = null;
    // Filter states
    this.filterType = 'all';
    this.searchQuery = '';
    this.radiusFilter = null; // in miles, null = show all (25 miles)
    // UI states
    this.isLoading = true;
    this.isConnected = false;
    this.error = null;
    this.isGettingLocation = false;
    this.locationEnabled = false;
    // User location for distance calculation
    this.userLocation = {
      lat: 33.4484,
      lng: -112.0740
    }; // Default: Phoenix, AZ
    this.subscriptions = [];
  }
  ngOnInit() {
    this.loadInitialData();
    this.subscribeToUpdates();
    this.subscribeToConnectionStatus();
  }
  ngOnDestroy() {
    this.subscriptions.forEach(sub => sub.unsubscribe());
  }
  /**
   * Request user location (triggered by button click)
   */
  requestLocation() {
    if (this.locationEnabled) {
      // Toggle off
      this.locationEnabled = false;
      this.userLocation = {
        lat: 33.4484,
        lng: -112.0740
      }; // Reset to default
      this.calculateDistances();
      this.applyFilters();
      return;
    }
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      return;
    }
    this.isGettingLocation = true;
    navigator.geolocation.getCurrentPosition(position => {
      this.userLocation = {
        lat: position.coords.latitude,
        lng: position.coords.longitude
      };
      this.locationEnabled = true;
      this.isGettingLocation = false;
      console.log('User location obtained:', this.userLocation);
      this.calculateDistances();
      this.applyFilters();
    }, error => {
      this.isGettingLocation = false;
      this.locationEnabled = false;
      let errorMessage = 'Unable to get your location. ';
      switch (error.code) {
        case error.PERMISSION_DENIED:
          errorMessage += 'Location permission denied. Please enable location access in your browser settings.';
          break;
        case error.POSITION_UNAVAILABLE:
          errorMessage += 'Location information unavailable.';
          break;
        case error.TIMEOUT:
          errorMessage += 'Location request timed out.';
          break;
        default:
          errorMessage += 'An unknown error occurred.';
      }
      alert(errorMessage);
      console.error('Location error:', error);
    }, {
      enableHighAccuracy: true,
      timeout: 10000,
      maximumAge: 0
    });
  }
  /**
   * Calculate distance for all parking lots
   */
  calculateDistances() {
    this.parkingLots = this.parkingLots.map(lot => ({
      ...lot,
      distance: this.kmToMiles(this.parkingService.calculateDistance(this.userLocation.lat, this.userLocation.lng, lot.location.lat, lot.location.lng))
    }));
  }
  /**
   * Convert kilometers to miles
   */
  kmToMiles(km) {
    return km * 0.621371; // 1 km = 0.621371 miles
  }
  /**
   * Load initial parking lot data
   */
  loadInitialData() {
    this.isLoading = true;
    this.error = null;
    this.parkingService.getAllParkingLots().subscribe({
      next: lots => {
        this.parkingLots = lots;
        this.calculateDistances();
        this.applyFilters();
        this.loadStats();
        this.isLoading = false;
      },
      error: err => {
        this.error = 'Failed to load parking data. Please try again.';
        this.isLoading = false;
        console.error('Error loading parking lots:', err);
      }
    });
  }
  /**
   * Load dashboard statistics
   */
  loadStats() {
    this.parkingService.getStats().subscribe({
      next: stats => {
        this.stats = stats;
      },
      error: err => {
        console.error('Error loading stats:', err);
      }
    });
  }
  /**
   * Subscribe to real-time parking updates
   */
  subscribeToUpdates() {
    const updateSub = this.parkingService.parkingUpdates$.subscribe({
      next: update => {
        const lot = this.parkingLots.find(l => l.id === update.lotId);
        if (lot) {
          lot.availableSpots = update.availableSpots;
          lot.lastUpdate = update.timestamp;
          this.applyFilters();
          this.loadStats();
        }
      }
    });
    this.subscriptions.push(updateSub);
  }
  /**
   * Subscribe to WebSocket connection status
   */
  subscribeToConnectionStatus() {
    const connectionSub = this.parkingService.connectionStatus$.subscribe({
      next: status => {
        this.isConnected = status;
      }
    });
    this.subscriptions.push(connectionSub);
  }
  /**
   * Set availability filter
   */
  setFilter(type) {
    this.filterType = type;
    this.applyFilters();
  }
  /**
   * Set radius filter
   */
  setRadiusFilter(radius) {
    this.radiusFilter = radius;
    this.applyFilters();
  }
  /**
   * Handle radius slider change
   */
  onRadiusSliderChange(event) {
    const value = parseFloat(event.target.value);
    // If slider is at max (25), treat as "show all"
    this.radiusFilter = value >= 25 ? null : value;
    this.applyFilters();
  }
  /**
   * Get slider percentage for visual fill
   */
  getSliderPercentage() {
    const value = this.radiusFilter || 25;
    return value / 25 * 100;
  }
  /**
   * Handle search input changes
   */
  onSearchChange(query) {
    this.searchQuery = query;
    this.applyFilters();
  }
  /**
   * Apply all active filters
   */
  applyFilters() {
    let filtered = [...this.parkingLots];
    // Apply availability filter
    if (this.filterType !== 'all') {
      filtered = filtered.filter(lot => {
        const occupancy = this.getOccupancyRate(lot);
        switch (this.filterType) {
          case 'available':
            return occupancy < 50;
          case 'moderate':
            return occupancy >= 50 && occupancy < 80;
          case 'full':
            return occupancy >= 80;
          default:
            return true;
        }
      });
    }
    // Apply radius filter
    if (this.radiusFilter !== null) {
      filtered = filtered.filter(lot => lot.distance !== undefined && lot.distance <= this.radiusFilter);
    }
    // Apply search filter
    if (this.searchQuery.trim()) {
      const query = this.searchQuery.toLowerCase();
      filtered = filtered.filter(lot => lot.name.toLowerCase().includes(query));
    }
    // Sort by distance if available
    if (filtered.every(lot => lot.distance !== undefined)) {
      filtered.sort((a, b) => (a.distance || 0) - (b.distance || 0));
    }
    this.filteredLots = filtered;
  }
  /**
   * Clear all filters
   */
  clearAllFilters() {
    this.filterType = 'all';
    this.searchQuery = '';
    this.radiusFilter = null;
    this.applyFilters();
  }
  /**
   * Select a parking lot to view details
   */
  selectLot(lot) {
    this.selectedLot = lot;
  }
  /**
