import { Component, OnInit, OnDestroy } from '@angular/core';
import { Subject, takeUntil } from 'rxjs';
import { ParkingService, ParkingLot, Stats } from '../../services/parking.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-parking-dashboard',
  templateUrl: './parking-dashboard.component.html',
  styleUrls: ['./parking-dashboard.component.css']
})
export class ParkingDashboardComponent implements OnInit, OnDestroy {
  parkingLots: ParkingLot[] = [];
  filteredLots: ParkingLot[] = [];
  selectedLot: ParkingLot | null = null;
  stats: Stats | null = null;

  isLoading = false;
  isConnected = false;
  error: string | null = null;

  filterType: 'all' | 'available' | 'moderate' | 'full' = 'all';
  searchQuery = '';

  // ADD RADIUS PROPERTIES
  selectedRadius = 5000; // Default 5km
  radiusOptions = [
    { label: '1 km', value: 1000 },
    { label: '5 km', value: 5000 },
    { label: '10 km', value: 10000 },
    { label: '25 km', value: 25000 },
    { label: '50 km', value: 50000 }
  ];

  private destroy$ = new Subject<void>();

  constructor(private parkingService: ParkingService, private router: Router) {}

  ngOnInit(): void {
    this.loadParkingLots();
    this.subscribeToRealtimeUpdates();
    this.monitorConnectionStatus();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
    this.parkingService.disconnect();
  }

  loadParkingLots(): void {
    this.isLoading = true;
    this.error = null;

    this.parkingService.getAllParkingLots()
      .pipe(takeUntil(this.destroy$))
      .subscribe({
        next: (lots) => {
          this.parkingLots = lots;
          this.applyFilters();
          this.calculateStatsFromLots();
          this.isLoading = false;

          lots.forEach(lot => this.parkingService.subscribeToParkingLot(lot.id));
        },
        error: (err) => {
          this.error = 'Failed to load parking lots. Please try again.';
          this.isLoading = false;
          console.error(err);
        }
      });
  }

  calculateStatsFromLots(): void {
    if (this.parkingLots.length > 0) {
      const totalSpots = this.parkingLots.reduce((sum, lot) => sum + lot.totalSpots, 0);
      const totalAvailable = this.parkingLots.reduce((sum, lot) => sum + lot.availableSpots, 0);
      const avgOccupancy = ((totalSpots - totalAvailable) / totalSpots * 100);

      this.stats = {
        totalParkingLots: this.parkingLots.length,
        totalSpots,
        totalAvailable,
        avgOccupancy: parseFloat(avgOccupancy.toFixed(1))
      };
    }
  }

  // ADD RADIUS CHANGE METHOD
  onRadiusChange(radius: number): void {
    this.selectedRadius = radius;
    this.parkingService.setRadius(radius);
    this.loadParkingLots();
  }

  // ADD USE MY LOCATION METHOD (BONUS)
  useMyLocation(): void {
    if (navigator.geolocation) {
      this.isLoading = true;
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          this.parkingService.setLocation(latitude, longitude);
          this.loadParkingLots();
        },
        (error) => {
          console.error('Geolocation error:', error);
          this.error = 'Could not get your location. Using default location.';
          this.isLoading = false;
        }
      );
    } else {
      this.error = 'Geolocation not supported by your browser.';
    }
  }

  subscribeToRealtimeUpdates(): void {
    this.parkingService.parkingUpdates$
      .pipe(takeUntil(this.destroy$))
      .subscribe({
        next: (update) => {
          const lotIndex = this.parkingLots.findIndex(lot => lot.id === update.lotId);
          if (lotIndex !== -1) {
            this.parkingLots[lotIndex] = {
              ...this.parkingLots[lotIndex],
              availableSpots: update.availableSpots,
              lastUpdate: update.timestamp
            };
            this.applyFilters();
            this.calculateStatsFromLots();
          }

          if (this.selectedLot?.id === update.lotId) {
            this.selectedLot = {
              ...this.selectedLot,
              availableSpots: update.availableSpots,
              lastUpdate: update.timestamp
            };
          }
        },
        error: (err) => console.error('Real-time update error:', err)
      });
  }

  monitorConnectionStatus(): void {
    this.parkingService.connectionStatus$
      .pipe(takeUntil(this.destroy$))
      .subscribe({ next: (status) => (this.isConnected = status) });
  }

  selectLot(lot: ParkingLot): void {
    this.router.navigate(['/parking', lot.id]);
  }

  refreshData(): void {
    this.loadParkingLots();
  }

  setFilter(filter: 'all' | 'available' | 'moderate' | 'full'): void {
    this.filterType = filter;
    this.applyFilters();
  }

  onSearchChange(query: string): void {
    this.searchQuery = query;
    this.applyFilters();
  }

  applyFilters(): void {
    let filtered = [...this.parkingLots];

    if (this.filterType !== 'all') {
      filtered = filtered.filter(lot => {
        const occupancy = this.getOccupancyRate(lot);
        switch (this.filterType) {
          case 'available': return occupancy < 50;
          case 'moderate':  return occupancy >= 50 && occupancy < 80;
          case 'full':      return occupancy >= 80;
          default: return true;
        }
      });
    }

    if (this.searchQuery.trim()) {
      const query = this.searchQuery.toLowerCase();
      filtered = filtered.filter(lot => lot.name.toLowerCase().includes(query));
    }

    this.filteredLots = filtered;
  }

  getOccupancyRate(lot: ParkingLot): number {
    return ((lot.totalSpots - lot.availableSpots) / lot.totalSpots) * 100;
  }

  getStatusLabel(lot: ParkingLot): string {
    const occupancy = this.getOccupancyRate(lot);
    if (occupancy < 50) return 'Available';
    if (occupancy < 80) return 'Moderate';
    return 'Full';
  }

  getStatusColor(lot: ParkingLot): string {
    const occupancy = this.getOccupancyRate(lot);
    if (occupancy < 50) return 'green';
    if (occupancy < 80) return 'yellow';
    return 'red';
  }

  formatLastUpdate(timestamp: string): string {
    const date = new Date(timestamp);
    return date.toLocaleTimeString();
  }

  getTimeDifference(lot: ParkingLot): number {
    return lot.predictedAvailability - lot.availableSpots;
  }

  getTrendIcon(lot: ParkingLot): string {
    const diff = this.getTimeDifference(lot);
    if (diff > 0) return '↑';
    if (diff < 0) return '↓';
    return '→';
  }

  getTrendMessage(lot: ParkingLot): string {
    const diff = this.getTimeDifference(lot);
    if (diff > 5) return 'More spots expected to open up soon!';
    if (diff < -5) return 'Spots are filling up. Consider arriving soon.';
    return 'Availability expected to remain stable.';
  }

  openInMaps(lot: ParkingLot): void {
    const url = `https://www.google.com/maps?q=${lot.location.lat},${lot.location.lng}`;
    window.open(url, '_blank');
  }

  getDirections(lot: ParkingLot): void {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const url = `https://www.google.com/maps/dir/${latitude},${longitude}/${lot.location.lat},${lot.location.lng}`;
          window.open(url, '_blank');
        },
        (error) => {
          console.error('Geolocation error:', error);
          this.openInMaps(lot);
        }
      );
    } else {
      this.openInMaps(lot);
    }
  }
}
